SELECT he.id, he.name, he.avatar, he.hospital_id AS hospitalId, he.title, he.intro, he.sort_order AS sortOrder, he.created_at AS createdAt, he.created_by As createdBy, he.consultation_hours AS consultationHours, mh.name AS hospitalName, sa.name AS accountName FROM m_appointment_hospital_expert he LEFT JOIN m_hospital mh ON mh.id = he.hospital_id LEFT JOIN sys_account sa ON sa.id = he.account_id
1 = 1
AND he.account_id = #{accountId}
AND he.hospital_id = #{hospitalId}
AND he.name LIKE CONCAT('%', #{name}, '%')

SELECT COUNT(*) FROM m_appointment_hospital_expert he
1 = 1
AND he.account_id = #{accountId}
AND he.hospital_id = #{hospitalId}
