mapper.select:
SELECT he.id, he.name, he.avatar, he.hospital_id AS hospitalId, he.title, he.domain, he.intro, he.created_at AS createdAt, he.created_by As createdBy, mh.name AS hospitalName, sa.name AS accountName FROM m_appointment_hospital_expert he LEFT JOIN m_hospital mh ON mh.id = he.hospital_id LEFT JOIN sys_account sa ON sa.id = he.account_id
he.account_id = #{accountId}
he.hospital_id = #{hospitalId}
AND he.name LIKE CONCAT('%', #{name}, '%')
